//如何傳遞參數
function Hello () {
  const clickHandler = (msg) => {
    console.log('函數組件中的事件被觸發了', msg)
  }
  return <div onClick={() => clickHandler('我是你爸')}>hello</div>
}

function App () {
  return (
    <div className="App">
      <Hello />
    </div>
  )
}

export default App
