// 組件狀態 類組件作為演示 (函數組件需要用到HOOK，等用到再說)

import React from 'react'

class TestComponent extends React.Component {
  state = {
    // 在這裡可以定義各種屬性 全都是當前狀態
    name: 'Gusty'
  }
  // this is TestComponent
  render () {
    return <div>{this.state.name}</div>
  }
}

function App () {
  return (
    <div className="App">
      <TestComponent />
    </div>
  )
}

export default App
