// 組件狀態 類組件作為演示 (函數組件需要用到HOOK，等用到再說)

import React from 'react'

class TestComponent extends React.Component {
  // 1.定義組件狀態
  state = {
    // 在這裡可以定義各種屬性 全都是當前狀態
    name: 'Gusty'
  }

  changeName = () => {
    // 3.修改state中的狀態name
    // 注意: 不可以直接做賦值修改 必須通過一個方法setState
    this.setState({
      name: 'Father'
    })
  }

  render () {
    // 2.使用狀態
    return (
      <div>
        this is TestComponent
        {this.state.name}
        <button onClick={this.changeName}>修改name</button>
      </div>
    )
  }
}

function App () {
  return (
    <div className="App">
      <TestComponent />
    </div>
  )
}

export default App
