// jsx樣式控制
// 1.行內樣式 - 在元素身上綁定一個style屬性即可

// 2.類明樣式 - 在元素身上綁定一個className屬性即可
import './app.css'

const style = {
  color:'red',
  fontSize:'30px'
}

function App() {
  return (
    <div className="App">
      {<span style={style}>this is span</span>}
    </div>
  );
}

export default App;
