import React from 'react'

// 通過類組件的方式修改Counter

class Counter extends React.Component {
  // 通過state定義組件狀態
  state = {
    count: 0
  }


  render () {
    return (
      <button >{this.state.count}</button>
    )
  }
}


// 根組件
function App () {
  return (
    <div className="App">
      <Counter />
    </div>
  )
}

export default App
