import React from 'react'

class Counter extends React.Component {
  // 1. 聲明用來控制input value的react組件自己的狀態
  state = {
    message: 'this is message'
  }


  render () {
    return (
      // 2. 給input框的value屬性綁定 react state
      <input type="text" value={this.state.message} />
    )
  }
}


// 根組件
function App () {
  return (
    <div className="App">
      <Counter />
    </div>
  )
}

export default App
